<?phpabstract class cController {    public $req = null;    public $res = null;    public function __construct() {        $this->req = Request::getContext();        $this->res = Response::getContext();        $pathprefix = $this->req->getPathPrefix();        $cusertype = getCurrentUserType();        if ($pathprefix == 'dashboard' && $cusertype != 'user') {            $this->res->redirect('main/login', 'Invalid Access');        }        if ($pathprefix == 'admin' && $cusertype != 'superadmin') {            $this->res->redirect('main/login', 'Invalid Access');        }    }}