<?phpfunction getUrl($path = null) {    if (PATH_URI != '/') {        return SITE_URI . PATH_URI . '/' . $path;    } else {        return SITE_URI . '/' . $path;    }}function setCurrentUser(array $userdata = array()) {    $sess = Session::getContext();    $sess->authUser = $userdata;}function getCurrentUser() {    $sess = Session::getContext();    return $sess->authUser;}function getCurrentUserID() {    $sess = Session::getContext();    return isset($sess->authUser['id']) ? $sess->authUser['id'] : '';}function getCurrentUserType() {    $sess = Session::getContext();    return isset($sess->authUser['perms']) ? $sess->authUser['perms'] : '';}function clean($inp = null) {    return strip_tags(trim($inp));}function getRequestIP() {    return $_SERVER['REMOTE_ADDR'];}function genUID() {    return substr(md5(uniqid(rand())), 0, 12);}