<?phpclass DB {    private static $_dbr = null;    public static function getContext() {        if (self :: $_dbr) {            return self :: $_dbr;        }        list($dbtype, $host, $user, $pass, $dbname) = unserialize(DB_CON);        $dsn = $dbtype . ':host=' . $host . ';dbname=' . $dbname;        self :: $_dbr = new PDO($dsn, $user, $pass);        self :: $_dbr->exec('SET NAMES utf8');        self :: $_dbr->setAttribute(PDO::ATTR_PERSISTENT, true);        self :: $_dbr->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_OBJ);        self :: $_dbr->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        self :: $_dbr->setAttribute(PDO::ATTR_EMULATE_PREPARES, false);        return self :: $_dbr;    }}