<?phpclass DB {    private static $_dbh = null;    public static function getContext() {        if (self :: $_dbh) {            return self :: $_dbh;        }        list($dbtype, $host, $user, $pass, $dbname) = unserialize(DB_CON);        $dsn = $dbtype . ':host=' . $host . ';dbname=' . $dbname;        self :: $_dbh = new PDO($dsn, $user, $pass);        self :: $_dbh->exec('SET NAMES utf8');        self :: $_dbh->setAttribute(PDO::ATTR_PERSISTENT, true);        self :: $_dbh->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_OBJ);        self :: $_dbh->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        self :: $_dbh->setAttribute(PDO::ATTR_EMULATE_PREPARES, false);        return self :: $_dbh;    }}