<?phpfinal class Session_Core {    public function __construct() {        @session_start();        if (SESS_TIMEOUT)            $this->_verifyInactivity(SESS_TIMEOUT);    }    public function __set($key, $value) {        $_SESSION[$key] = $value;    }    public function __get($key) {        return isset($_SESSION[$key]) ? $_SESSION[$key] : '';    }    public function getId() {        return session_id();    }    public function delete($key) {        unset($_SESSION[$key]);    }    public function destroy() {        session_destroy();    }    private function _verifyInactivity($maxtime) {        if (!$this->activity_time) {            $this->activity_time = time();        }        if ((time() - $this->activity_time) > $maxtime) {            $this->destroy();        } else {            $this->activity_time = time();        }    }}