<?phperror_reporting(0);define('APP_DIR', realpath(dirname(__FILE__)) . '/app/');define('CONT_DIR', APP_DIR . 'controllers/');define('LIBS_DIR', APP_DIR . 'libraries/');define('VIEW_DIR', APP_DIR . 'views/');define('MODS_DIR', APP_DIR . 'models/');@require_once APP_DIR . 'coreclasses.php';date_default_timezone_set(DEFAULT_TIMEZONE);mb_internal_encoding("UTF-8");mb_http_output("UTF-8");$request = Request::getContext();$response = Response::getContext();try {    Application::run($request);} catch (Exception $e) {    $data['message'] = DEBUG ? $e : '';    if ($request->isAjax()) {        $data['layout'] = false;    }    $response->display($data, 'errors/exception');    exit;}